import React, { useState, useEffect } from 'react';
import { ethers } from 'ethers';
import { motion } from 'framer-motion';
import './App.css';

// Replace with your deployed contract address (from Truffle migration):
const contractAddress = "0x5041dc60397fe2c2bC8e8482AfA90277F30D2aBd";

const contractABI = [
  {
    "inputs": [],
    "stateMutability": "nonpayable",
    "type": "constructor"
  },
  {
    "inputs": [{"internalType": "address", "name": "_voter", "type": "address"}],
    "name": "assignVoter",
    "outputs": [],
    "stateMutability": "nonpayable",
    "type": "function"
  },
  {
    "inputs": [
      {"internalType": "address payable", "name": "_to", "type": "address"},
      {"internalType": "uint256", "name": "amountToTransfer", "type": "uint256"}
    ],
    "name": "transferFunds",
    "outputs": [],
    "stateMutability": "payable",
    "type": "function"
  },
  {
    "inputs": [{"internalType": "address payable", "name": "_newOwner", "type": "address"}],
    "name": "voteForNewOwner",
    "outputs": [],
    "stateMutability": "nonpayable",
    "type": "function"
  },
  {
    "inputs": [
      {"internalType": "address", "name": "_whoCanSend", "type": "address"},
      {"internalType": "uint256", "name": "_restriction", "type": "uint256"}
    ],
    "name": "setLimit",
    "outputs": [],
    "stateMutability": "nonpayable",
    "type": "function"
  },
  {
    "inputs": [],
    "name": "owner",
    "outputs": [{"internalType": "address payable", "name": "", "type": "address"}],
    "stateMutability": "view",
    "type": "function"
  },
  {
    "stateMutability": "payable",
    "type": "receive"
  },
  {
    "inputs": [],
    "name": "deposit",
    "outputs": [],
    "stateMutability": "payable",
    "type": "function"
  }
];

function App() {
  const [provider, setProvider] = useState(null);
  const [walletAddress, setWalletAddress] = useState("");
  const [contract, setContract] = useState(null);
  const [balance, setBalance] = useState("");
  const [depositAmount, setDepositAmount] = useState("");
  const [transferAmount, setTransferAmount] = useState("");
  const [transferTo, setTransferTo] = useState("");
  const [voterAddress, setVoterAddress] = useState("");
  const [newOwnerAddress, setNewOwnerAddress] = useState("");
  const [setLimitAddress, setSetLimitAddress] = useState("");
  const [limitAmount, setLimitAmount] = useState("");

  async function connectWallet() {
    if (window.ethereum) {
      try {
        await window.ethereum.request({ method: "eth_requestAccounts" });
        const tempProvider = new ethers.providers.Web3Provider(window.ethereum);
        setProvider(tempProvider);
        const signer = tempProvider.getSigner();
        const address = await signer.getAddress();
        setWalletAddress(address);
        const tempContract = new ethers.Contract(contractAddress, contractABI, signer);
        setContract(tempContract);
        fetchBalance(tempProvider);
        const ownerAddress = await tempContract.owner();
        setContractOwner(ownerAddress);
      } catch (err) {
        console.error(err);
      }
    } else {
      alert("Please install MetaMask!");
    }
  }

  async function fetchBalance(providerInstance) {
    if (providerInstance && contract) {
      try {
        const bal = await providerInstance.getBalance(contract.address);
        setBalance(ethers.utils.formatEther(bal));
      } catch (error) {
        console.error(error);
      }
    }
  }

  async function depositFunds() {
    if (!contract) return;
    try {
      const tx = await contract.deposit({
        value: ethers.utils.parseEther(depositAmount)
      });
      await tx.wait();
      alert("Deposit successful!");
      fetchBalance(provider);
    } catch (error) {
      console.error(error);
      alert("Deposit failed!");
    }
  }

  async function transferFunds() {
    if (!contract) return;
    try {
      const tx = await contract.transferFunds(
        transferTo,
        ethers.utils.parseEther(transferAmount),
        { value: 0 }
      );
      await tx.wait();
      alert("Transfer successful!");
      fetchBalance(provider);
    } catch (error) {
      console.error(error);
      alert("Transfer failed!");
    }
  }

  async function assignVoterFunc() {
    if (!contract) return;
    try {
      const tx = await contract.assignVoter(voterAddress);
      await tx.wait();
      alert("Voter assigned successfully!");
    } catch (error) {
      console.error(error);
      alert("Assigning voter failed!");
    }
  }

  async function voteNewOwner() {
    if (!contract) return;
    try {
      const tx = await contract.voteForNewOwner(newOwnerAddress);
      await tx.wait();
      alert("Vote cast successfully!");
    } catch (error) {
      console.error(error);
      alert("Voting failed!");
    }
  }

  async function setLimitFunc() {
    if (!contract) return;
    try {
      const tx = await contract.setLimit(
        setLimitAddress,
        ethers.BigNumber.from(limitAmount)
      );
      await tx.wait();
      alert("Limit set successfully!");
    } catch (error) {
      console.error(error);
      alert("Setting limit failed!");
    }
  }

  useEffect(() => {
    if (provider && contract) {
      fetchBalance(provider);
    }
  }, [provider, contract]);

  return (
    <div className="App">
      <motion.h1
        initial={{ opacity: 0, y: -50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
      >
        Smart Wallet
      </motion.h1>

      {walletAddress ? (
        <motion.p
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.3, duration: 0.6 }}
        >
          Connected: {walletAddress}
        </motion.p>
      ) : (
        <motion.button
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          onClick={connectWallet}
        >
          Connect Wallet
        </motion.button>
      )}

      <motion.h2
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.5, duration: 0.6 }}
      >
        Contract Balance: {balance} ETH
      </motion.h2>

      {contractOwner && (
        <motion.h3 initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ delay: 0.45, duration: 0.6 }}>
          Contract Owner: {contractOwner}
        </motion.h3>
      )}

      <motion.div
        className="section"
        initial={{ opacity: 0, x: -100 }}
        animate={{ opacity: 1, x: 0 }}
        transition={{ duration: 0.6 }}
      >
        <h3>Deposit Funds</h3>
        <input
          type="text"
          placeholder="Amount in ETH"
          value={depositAmount}
          onChange={(e) => setDepositAmount(e.target.value)}
        />
        <motion.button
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          onClick={depositFunds}
        >
          Deposit
        </motion.button>
      </motion.div>

      <motion.div
        className="section"
        initial={{ opacity: 0, x: 100 }}
        animate={{ opacity: 1, x: 0 }}
        transition={{ duration: 0.6 }}
      >
        <h3>Transfer Funds</h3>
        <input
          type="text"
          placeholder="Recipient Address"
          value={transferTo}
          onChange={(e) => setTransferTo(e.target.value)}
        />
        <input
          type="text"
          placeholder="Amount in ETH"
          value={transferAmount}
          onChange={(e) => setTransferAmount(e.target.value)}
        />
        <motion.button
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          onClick={transferFunds}
        >
          Transfer
        </motion.button>
      </motion.div>

      <motion.div
        className="section"
        initial={{ opacity: 0, scale: 0.8 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ duration: 0.6 }}
      >
        <h3>Assign Voter (Owner Only)</h3>
        <input
          type="text"
          placeholder="Voter Address"
          value={voterAddress}
          onChange={(e) => setVoterAddress(e.target.value)}
        />
        <motion.button
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          onClick={assignVoterFunc}
        >
          Assign Voter
        </motion.button>
      </motion.div>

      <motion.div
        className="section"
        initial={{ opacity: 0, scale: 0.8 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ duration: 0.6 }}
      >
        <h3>Vote for New Owner</h3>
        <input
          type="text"
          placeholder="New Owner Address"
          value={newOwnerAddress}
          onChange={(e) => setNewOwnerAddress(e.target.value)}
        />
        <motion.button
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          onClick={voteNewOwner}
        >
          Vote
        </motion.button>
      </motion.div>

      <motion.div
        className="section"
        initial={{ opacity: 0, scale: 0.8 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ duration: 0.6 }}
      >
        <h3>Set Transfer Limit (Owner Only)</h3>
        <input
          type="text"
          placeholder="Address"
          value={setLimitAddress}
          onChange={(e) => setSetLimitAddress(e.target.value)}
        />
        <input
          type="number"
          placeholder="Limit Amount"
          value={limitAmount}
          onChange={(e) => setLimitAmount(e.target.value)}
        />
        <motion.button
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          onClick={setLimitFunc}
        >
          Set Limit
        </motion.button>
      </motion.div>
    </div>
  );
}

export default App;
