import React, { useState, useEffect } from 'react';
import { Typewriter } from 'react-simple-typewriter';
import { motion } from 'framer-motion';
import './Home.css';

function Home({ walletAddress }) {
  const [showTypewriter, setShowTypewriter] = useState(false);

  // Delay the start of the typewriter effect by 1 second
  useEffect(() => {
    const timer = setTimeout(() => setShowTypewriter(true), 1000);
    return () => clearTimeout(timer);
  }, []);

  return (
    <div className="main-container home-container">
      {/* New GIF placed above the welcome text */}
      <motion.img 
        src="/home-page-new.gif" 
        alt="Futuristic Animation"
        style={{ width: '300px', height: '300px', marginBottom: '20px' }}
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.5, duration: 1, ease: "easeInOut" }}
      />

      {showTypewriter && (
        <h2 className="typewriter">
          <Typewriter
            words={[`Welcome ${walletAddress}`]}
            loop={1}
            cursor
            cursorStyle="|"
            typeSpeed={70}
            delaySpeed={1000}
          />
        </h2>
      )}
    </div>
  );
}

export default Home;
