// src/components/Deposit.js
import React, { useState } from 'react';
import { ethers } from 'ethers';
import { motion } from 'framer-motion';

function Deposit({ contract }) {
  const [depositAmount, setDepositAmount] = useState("");

  async function handleDeposit() {
    try {
      const tx = await contract.deposit({
        value: ethers.utils.parseEther(depositAmount)
      });
      await tx.wait();
      alert("Deposit successful!");
    } catch (err) {
      console.error(err);
      alert("Deposit failed!");
    }
  }

  return (
    <div className="deposit-page-container">
      {/* Left Side */}
      <div className="deposit-left">
        <h1>Deposit money into the Wallet</h1>
      </div>

      {/* Right Side */}
      <div className="deposit-right">
        <div className="deposit-gif-container">
          <img src="/deposit-new-.gif" alt="Deposit Gif" className="deposit-gif" />
        </div>
        <div className="deposit-input-container">
          <Input 
            placeholder="Amount in ETH" 
            name="depositAmount" 
            type="number" 
            value={depositAmount} 
            handleChange={(e, name) => setDepositAmount(e.target.value)}
          />
          <button onClick={handleDeposit}>Deposit</button>
        </div>
      </div>
    </div>
  );
}

export default Deposit;
