// src/components/Deposit.js
import React, { useState, useEffect } from 'react';
import { ethers } from 'ethers';
import { motion } from 'framer-motion';

function Deposit({ contract }) {
  const [depositAmount, setDepositAmount] = useState("");
  const [userDepositTotal, setUserDepositTotal] = useState(0);

  // Load saved deposit total from localStorage when the component mounts
  useEffect(() => {
    const savedDepositTotal = localStorage.getItem('userDepositTotal');
    if (savedDepositTotal) {
      setUserDepositTotal(parseFloat(savedDepositTotal));
    }
  }, []);

  // const Input = ({placeholder, name, type, value, handleChange}) => ( 
  //   <input 
  //     placeholder ={palceholder}
  //     type={type}
  //     step="0.0001
  //     value={value}
  //     onChange={(e) => handleChange(e, name)}
  //     className="my-2 w-full rounded-sm p-2 outline-none bg-transparent text-white border-none text-small white-glassmorphism"
  //   />
  // );

  async function handleDeposit() {
    try {
      const tx = await contract.deposit({
        value: ethers.utils.parseEther(depositAmount)
      });
      await tx.wait();
      alert("Deposit successful!");

      // Update the deposit total locally
      const newTotal = userDepositTotal + parseFloat(depositAmount);
      setUserDepositTotal(newTotal);
      localStorage.setItem('userDepositTotal', newTotal);

      // Clear the input field
      setDepositAmount("");
    } catch (err) {
      console.error(err);
      alert("Deposit failed!");
    }
  }

  return (
    <div className="deposit-page-container">
      <div className="deposit-left">
        <h1>Deposit money into the Wallet</h1>
        <div class="total-deposit">
          <motion.div
            key={userDepositTotal} // key triggers re-animation on change
            initial={{ opacity: 0, x: -50 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.5, ease: "easeInOut" }}
            className="user-deposit-total"
          >
          </motion.div>
            <h3>Total Money Deposited: {userDepositTotal}</h3>
          </div>
      </div>
      <div className="deposit-right">
      <motion.img 
        src="/deposit-new.gif" 
        alt="Deposit Animation" 
        style={{ width: "150px", height: "150px", marginBottom: "20px" }}
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.2, duration: 1, ease: "easeInOut" }}
      />
      <h2>Deposit Funds</h2>
      <div class="glass-container">
      <input class = "glass-input"
        type="text" 
        placeholder="Amount in ETH" 
        value={depositAmount} 
        onChange={(e) => setDepositAmount(e.target.value)} 
      />

      <button clsasName="glass-button" onClick={handleDeposit}>Deposit</button>
      </div>
      </div>
    </div>
  );
}

export default Deposit;
