import React, { useState } from 'react';
import { ethers } from 'ethers';
import { motion } from 'framer-motion';
import Input from './Input'; // adjust path as needed

function Deposit({ contract }) {
  const [depositAmount, setDepositAmount] = useState("");

  const handleChange = (e, name) => {
    // For now, we assume there's only one input.
    setDepositAmount(e.target.value);
  };

  const handleDeposit = async () => {
    try {
      const tx = await contract.deposit({
        value: ethers.utils.parseEther(depositAmount)
      });
      await tx.wait();
      alert("Deposit successful!");
      setDepositAmount("");
    } catch (err) {
      console.error(err);
      alert("Deposit failed!");
    }
  };

  return (
    <div className="p-5 sm:w-96 w-full flex flex-col justify-start items-center blue-glassmorphism">
      <motion.img 
              src="/deposit-new.gif" 
              alt="Deposit Animation" 
              style={{ width: "150px", height: "150px", marginBottom: "20px" }}
              initial={{ opacity: 0, y: -20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.2, duration: 1, ease: "easeInOut" }}
            />
      <Input 
        placeholder="Amount in ETH" 
        name="depositAmount" 
        type="number" 
        value={depositAmount} 
        handleChange={handleChange} 
      />
      <button onClick={handleDeposit} className="mt-4 px-4 py-2 bg-blue-600 text-white rounded">
        Deposit
      </button>
    </div>
  );
}

export default Deposit;
