import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

function Members() {
  const [accessList, setAccessList] = useState({});
  const [showAccessList, setShowAccessList] = useState(false);

  useEffect(() => {
    const storedAccessList = localStorage.getItem('accessList');
    if (storedAccessList) {
      try {
        const parsedList = JSON.parse(storedAccessList);
        setAccessList(parsedList);
      } catch (err) {
        console.error("Error parsing accessList:", err);
      }
    }
  }, []);

  const handleToggleAccessList = () => {
    setShowAccessList(prev => !prev);
  };

  // Filter out only allowed users
  const allowedUsers = Object.entries(accessList)
    .filter(([address, allowed]) => allowed)
    .map(([address]) => address);

  // Animation for reveal from the top
  const animationProps =
     {
      initial: { x: -100, opacity: 0 },
      animate: { x: 0, opacity: 1 },
      exit: { x: 100, opacity: 0 },
      transition: { delay: 0.2, duration: 1.0, ease: "easeInOut" }
      }

  return (
    <div className="members-container">
      <h1>Members</h1>
      <button className="access-btn" onClick={handleToggleAccessList}>
        {showAccessList ? "Hide Members List" : "Show Members List"}
      </button>
      <AnimatePresence>
        {showAccessList && (
          <motion.div key="access-list" {...animationProps} className="access-list">
            <h2>Users with Access:</h2>
            {allowedUsers.length === 0 ? (
              <p>No users have been granted access yet.</p>
            ) : (
              <ul>
                {allowedUsers.map((address, index) => (
                  <li key={index}>{address}</li>
                ))}
              </ul>
            )}
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
}

export default Members;
