import React, { useState, useEffect } from 'react';
import { useTypewriter, Cursor } from 'react-simple-typewriter';
import './Home.css';

function Home({ walletAddress }) {
  const [showText, setShowText] = useState(false);

  // Delay the start of the typewriter effect by 1 second (1000 ms)
  useEffect(() => {
    const timer = setTimeout(() => setShowText(true), 1500);
    return () => clearTimeout(timer);
  }, []);

  const [text] = useTypewriter({
    words: ['Welcome to Smart Wallet!', 'Your gateway to the future...'],
    loop: 1,           // type the text only once
    typeSpeed: 50,     // typing speed in ms per character
    deleteSpeed: 50,   // not used if loop is 0
    delaySpeed: 2000,  // delay before ending the animation
  });

  return (
    <div className="main-container home-container">
      {showText ? (
        <h2 className="typewriter">
          {text}
          <Cursor cursorColor="#0070f3" />
        </h2>
      ) : (
        // Optionally show a placeholder or nothing during the delay
        <h2 className="typewriter">&nbsp;</h2>
      )}
      <p className="welcome-msg">
        Welcome, {walletAddress}! We are delighted to have you here.
      </p>
    </div>
  );
}

export default Home;
