// src/components/Home.js
import React from 'react';
import { useTypewriter, Cursor } from 'react-simple-typewriter';
import './Home.css'; // optional: for any additional Home-specific styles

function Home({ walletAddress }) {
  // useTypewriter hook with desired settings.
  const [text] = useTypewriter({
    words: ['Welcome to Smart Wallet!', 'Your gateway to the future...'],
    loop: 1,           // type the text only once (or set to 0 for infinite)
    typeSpeed: 70,     // typing speed in ms per character
    deleteSpeed: 50,   // delete speed if looping (not used if loop:1)
    delaySpeed: 2000,  // delay before deleting or finishing
  });

  return (
    <div className="main-container home-container">
      <h2 className="typewriter">
        {text}
        <Cursor cursorColor="#0070f3" />
      </h2>
      <p className="welcome-msg">
        Welcome, {walletAddress}! We are delighted to have you here.
      </p>
    </div>
  );
}

export default Home;
