import React, { useState, useEffect } from 'react';
import { ethers } from 'ethers';
import { BrowserRouter as Router, Routes, Route, Link, Navigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import './App.css';

// Import feature components
import Home from './components/Home';
import Deposit from './components/Deposit';
import Transfer from './components/Transfer';
import AssignVoter from './components/AssignVoter';
import VoteNewOwner from './components/VoteNewOwner';
import SetLimit from './components/SetLimit';
import Admin from './components/Admin';

// Replace with your deployed contract address (from Truffle migration)
const contractAddress = "0x5041dc60397fe2c2bC8e8482AfA90277F30D2aBd";

const contractABI = [
  {
    "inputs": [],
    "stateMutability": "nonpayable",
    "type": "constructor"
  },
  {
    "inputs": [{"internalType": "address", "name": "_voter", "type": "address"}],
    "name": "assignVoter",
    "outputs": [],
    "stateMutability": "nonpayable",
    "type": "function"
  },
  {
    "inputs": [
      {"internalType": "address payable", "name": "_to", "type": "address"},
      {"internalType": "uint256", "name": "amountToTransfer", "type": "uint256"}
    ],
    "name": "transferFunds",
    "outputs": [],
    "stateMutability": "payable",
    "type": "function"
  },
  {
    "inputs": [{"internalType": "address payable", "name": "_newOwner", "type": "address"}],
    "name": "voteForNewOwner",
    "outputs": [],
    "stateMutability": "nonpayable",
    "type": "function"
  },
  {
    "inputs": [
      {"internalType": "address", "name": "_whoCanSend", "type": "address"},
      {"internalType": "uint256", "name": "_restriction", "type": "uint256"}
    ],
    "name": "setLimit",
    "outputs": [],
    "stateMutability": "nonpayable",
    "type": "function"
  },
  {
    "inputs": [],
    "name": "owner",
    "outputs": [{"internalType": "address payable", "name": "", "type": "address"}],
    "stateMutability": "view",
    "type": "function"
  },
  {
    "stateMutability": "payable",
    "type": "receive"
  },
  {
    "inputs": [],
    "name": "deposit",
    "outputs": [],
    "stateMutability": "payable",
    "type": "function"
  },
  // Added functions for access control:
  {
    "inputs": [],
    "name": "accessAllowed",
    "outputs": [{"internalType": "bool", "name": "", "type": "bool"}],
    "stateMutability": "view",
    "type": "function"
  },
  {
    "inputs": [
      {"internalType": "address", "name": "user", "type": "address"},
      {"internalType": "bool", "name": "allowed", "type": "bool"}
    ],
    "name": "setAccess",
    "outputs": [],
    "stateMutability": "nonpayable",
    "type": "function"
  }
];

function App() {
  const [provider, setProvider] = useState(null);
  const [walletAddress, setWalletAddress] = useState("");
  const [contract, setContract] = useState(null);
  const [accessAllowed, setAccessAllowed] = useState(false);
  const [loading, setLoading] = useState(true);

  async function connectWallet() {
    if (window.ethereum) {
      try {
        await window.ethereum.request({ method: "eth_requestAccounts" });
        const tempProvider = new ethers.providers.Web3Provider(window.ethereum);
        setProvider(tempProvider);
        const signer = tempProvider.getSigner();
        const address = await signer.getAddress();
        setWalletAddress(address);
        const tempContract = new ethers.Contract(contractAddress, contractABI, signer);
        setContract(tempContract);

        // Check if the connected account is allowed access
        const allowed = await tempContract.accessAllowed();
        setAccessAllowed(allowed);
        setLoading(false);
      } catch (err) {
        console.error("Error connecting wallet:", err);
      }
    } else {
      alert("Please install MetaMask!");
    }
  }

  // Initially, show a home/login page with "Connect Wallet" button.
  if (loading) {
    return (
      <div className="App">
        <motion.h1 initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 0.6 }}>
          Welcome to Smart Wallet
        </motion.h1>
        <motion.button
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          onClick={connectWallet}
        >
          Connect Wallet
        </motion.button>
      </div>
    );
  }

  // If wallet is connected but access is not allowed, show Access Denied
  if (!accessAllowed) {
    return (
      <div className="App">
        <h1>Access Denied</h1>
        <p>Your account ({walletAddress}) is not authorized to access this wallet.</p>
      </div>
    );
  }

  return (
    <Router>
      <div className="App">
        <NavBar />
        <Routes>
          <Route path="/" element={<Home walletAddress={walletAddress} />} />
          <Route path="/deposit" element={<Deposit contract={contract} />} />
          <Route path="/transfer" element={<Transfer contract={contract} />} />
          <Route path="/assign-voter" element={<AssignVoter contract={contract} />} />
          <Route path="/vote-new-owner" element={<VoteNewOwner contract={contract} />} />
          <Route path="/set-limit" element={<SetLimit contract={contract} />} />
          <Route path="/admin" element={<Admin contract={contract} />} />
          <Route path="*" element={<Navigate to="/" />} />
        </Routes>
      </div>
    </Router>
  );
}

function NavBar() {
  return (
    <nav style={{ marginBottom: "20px" }}>
      <Link to="/">Home</Link> |{" "}
      <Link to="/deposit">Deposit</Link> |{" "}
      <Link to="/transfer">Transfer</Link> |{" "}
      <Link to="/assign-voter">Assign Voter</Link> |{" "}
      <Link to="/vote-new-owner">Vote New Owner</Link> |{" "}
      <Link to="/set-limit">Set Limit</Link> |{" "}
      <Link to="/admin">Admin</Link>
    </nav>
  );
}

export default App;
