// src/components/Admin.js
import React, { useState } from 'react';
import { motion } from 'framer-motion';

function Admin({ contract }) {
  const [accessAddress, setAccessAddress] = useState("");
  const [accessAllowed, setAccessAllowed] = useState("");

  async function handleSetAccess() {
    try {
      const allowed = accessAllowed.toLowerCase() === "true";
      const tx = await contract.setAccess(accessAddress, allowed);
      await tx.wait();
      alert("Access set successfully!");
    } catch (err) {
      console.error(err);
      alert("Setting access failed!");
    }
  }

  return (
    <div className="main-container">
      <motion.img 
        src="/admin.gif" 
        alt="Admin Animation" 
        style={{ width: "150px", height: "150px", marginBottom: "20px" }}
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.2, duration: 1, ease: "easeInOut" }}
      />
      <h2>Admin: Set Access Restrictions</h2>
      <input 
        type="text" 
        placeholder="Address" 
        value={accessAddress} 
        onChange={(e) => setAccessAddress(e.target.value)} 
      />
      <input 
        type="text" 
        placeholder="Allowed? (true/false)" 
        value={accessAllowed} 
        onChange={(e) => setAccessAllowed(e.target.value)} 
      />
      <button onClick={handleSetAccess}>Set Access</button>
    </div>
  );
}

export default Admin;
