// src/components/Admin.js
import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

function Admin({ contract }) {
  const [accessAddress, setAccessAddress] = useState("");
  const [accessAllowed, setAccessAllowed] = useState("");

  const [accessList, setAccessList] = useState({});
  const [showAccessList, setShowAccessList] = useState(false);

  async function handleSetAccess() {
    try {
      const allowed = accessAllowed.toLowerCase() === "true";
      const tx = await contract.setAccess(accessAddress, allowed);
      await tx.wait();
      alert("Access set successfully!");

      // Retrieve the current access list from localStorage or initialize as an object
    let accessList = JSON.parse(localStorage.getItem('accessList')) || {};

    // Update the access list with the new value for accessAddress
    accessList[accessAddress] = allowed;

    // Save the updated list back to localStorage
    localStorage.setItem('accessList', JSON.stringify(accessList));

    } catch (err) {
      console.error(err);
      alert("Setting access failed!");
    }
  }

  // Load access list from localStorage when the component mounts
  useEffect(() => {
    const storedAccessList = localStorage.getItem('accessList');
    if (storedAccessList) {
      try {
        const parsedList = JSON.parse(storedAccessList);
        setAccessList(parsedList);
      } catch (err) {
        console.error("Error parsing accessList from localStorage:", err);
      }
    }
  }, []);

  // Toggle showing the access list
  const handleToggleAccessList = () => {
    setShowAccessList((prev) => !prev);
    // Optionally, refresh the list from localStorage if it may have changed:
    const storedAccessList = localStorage.getItem('accessList');
    if (storedAccessList) {
      try {
        const parsedList = JSON.parse(storedAccessList);
        setAccessList(parsedList);
      } catch (err) {
        console.error("Error parsing accessList from localStorage:", err);
      }
    }
  };

  // Filter to get only users with access (true)
  const allowedUsers = Object.entries(accessList)
    .filter(([address, allowed]) => allowed)
    .map(([address]) => address);

    const animationProps = {
      initial: { y: -100, opacity: 0 },
      animate: { y: 0, opacity: 1 },
      exit: { y: 100, opacity: 0 },
      transition: { delay: 0.2, duration: 1.0, ease: "easeInOut" }
   }


  return (
    <div className="admin-container">
      <div className="admin-left">
        <h1>Set Access Restrictions for each user</h1>
      <button onClick={handleToggleAccessList}>
        {showAccessList ? "Hide Access List" : "Show Access List"}
      </button>
      <AnimatePresence>
      {showAccessList && (
        <motion.div 
        key={accessList}
        {...animationProps}
        >
          <h2>Users with Access:</h2>
          {allowedUsers.length === 0 ? (
            <p>No users have been granted access yet.</p>
          ) : (
            <ul>
              {allowedUsers.map((address, index) => (
                <li key={index}>{address}</li>
              ))}
            </ul>

          )}
        </motion.div>
      )}
      </AnimatePresence>
      </div>
      <div className="admin-right">
      <motion.img 
        src="/admin.gif" 
        alt="Admin Animation" 
        style={{ width: "150px", height: "150px", marginBottom: "20px" }}
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.2, duration: 1, ease: "easeInOut" }}
      />
      <h2>Set Access Restrictions</h2>
      <div className='glass-container'>
      <input className='glass-input'
        type="text" 
        placeholder="Address" 
        value={accessAddress} 
        onChange={(e) => setAccessAddress(e.target.value)} 
      />
      <input className='glass-input'
        type="text" 
        placeholder="Allowed? (true/false)" 
        value={accessAllowed} 
        onChange={(e) => setAccessAllowed(e.target.value)} 
      />
      <button onClick={handleSetAccess}>Set Access</button>
    </div>
    </div>
    </div>
  );
}

export default Admin;
