import React, { useState } from 'react';
import { ethers } from 'ethers';
import { BrowserRouter as Router, Routes, Route, Link, Navigate } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';
import './App.css';
import './index.css';

// Import your feature components (make sure these files exist in src/components)
import Home from './components/Home';
import Deposit from './components/Deposit';
import Transfer from './components/Transfer';
import AssignVoter from './components/AssignVoter';
import VoteNewOwner from './components/VoteNewOwner';
import SetLimit from './components/SetLimit';
import Admin from './components/Admin';
import Dropdown from './components/UserDropdown';

const contractAddress = "0xaaDf71540aFA13F952434bED512Cc29A1d9c2AE7";

const contractABI = [
  { "inputs": [], "stateMutability": "nonpayable", "type": "constructor" },
  { "inputs": [{ "internalType": "address", "name": "_voter", "type": "address" }], "name": "assignVoter", "outputs": [], "stateMutability": "nonpayable", "type": "function" },
  { "inputs": [{ "internalType": "address payable", "name": "_to", "type": "address" }, { "internalType": "uint256", "name": "amountToTransfer", "type": "uint256" }], "name": "transferFunds", "outputs": [], "stateMutability": "payable", "type": "function" },
  { "inputs": [{ "internalType": "address payable", "name": "_newOwner", "type": "address" }], "name": "voteForNewOwner", "outputs": [], "stateMutability": "nonpayable", "type": "function" },
  { "inputs": [{ "internalType": "address", "name": "_whoCanSend", "type": "address" }, { "internalType": "uint256", "name": "_restriction", "type": "uint256" }], "name": "setLimit", "outputs": [], "stateMutability": "nonpayable", "type": "function" },
  { "inputs": [], "name": "owner", "outputs": [{ "internalType": "address payable", "name": "", "type": "address" }], "stateMutability": "view", "type": "function" },
  { "stateMutability": "payable", "type": "receive" },
  { "inputs": [], "name": "deposit", "outputs": [], "stateMutability": "payable", "type": "function" },
  // Access control functions:
  { "inputs": [], "name": "isAllowed", "outputs": [{ "internalType": "bool", "name": "", "type": "bool" }], "stateMutability": "view", "type": "function" },
  { "inputs": [{ "internalType": "address", "name": "user", "type": "address" }, { "internalType": "bool", "name": "allowed", "type": "bool" }], "name": "setAccess", "outputs": [], "stateMutability": "nonpayable", "type": "function" }
];

function App() {
  const [provider, setProvider] = useState(null);
  const [walletAddress, setWalletAddress] = useState("");
  const [contract, setContract] = useState(null);
  const [accessAllowed, setAccessAllowed] = useState(false);
  const [loading, setLoading] = useState(true);
  const [showDropdown, setShowDropdown] = useState(false);
  const [accountBalance, setAccountBalance] = useState("");

  async function connectWallet() {
    if (window.ethereum) {
      try {
        await window.ethereum.request({ method: "eth_requestAccounts" });
        const tempProvider = new ethers.providers.Web3Provider(window.ethereum);
        setProvider(tempProvider);
        const signer = tempProvider.getSigner();
        const address = await signer.getAddress();
        setWalletAddress(address);
        const tempContract = new ethers.Contract(contractAddress, contractABI, signer);
        setContract(tempContract);
        // Check access using isAllowed()
        const allowed = await tempContract.isAllowed();
        setAccessAllowed(allowed);
        // Fetch account balance for dropdown
        const accBal = await tempProvider.getBalance(address);
        setAccountBalance(ethers.utils.formatEther(accBal));
        // End loading state, triggering transition
        setLoading(false);
      } catch (err) {
        console.error("Error connecting wallet:", err);
      }
    } else {
      alert("Please install MetaMask!");
    }
  }

  return (
    <Router>
      <AnimatePresence>
        {loading ? (
          <motion.div
            key="login"
            className="login-container"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0, transition: { duration: 1 } }}
          >
            <motion.h1
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ duration: 1.5, ease: "easeInOut" }}
            >
              Welcome to Smart Wallet
            </motion.h1>
            <motion.button
              className="connect-btn"
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={connectWallet}
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ delay: 0.5, duration: 1.5, ease: "easeInOut" }}
            >
              Connect Wallet
            </motion.button>
          </motion.div>
        ) : !accessAllowed ? (
          <motion.div
            key="denied"
            className="login-container"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0, transition: { duration: 1 } }}
          >
            <h1>Access Denied</h1>
            <p>Your account ({walletAddress}) is not authorized to access this wallet.</p>
          </motion.div>
        ) : (
          <motion.div
            key="main"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0, transition: { duration: 1 } }}
          >
            <UserDropdown 
              walletAddress={walletAddress} 
              accountBalance={accountBalance} 
              showDropdown={showDropdown} 
              setShowDropdown={setShowDropdown}
            />
            <NavBar />
            <Routes>
              <Route path="/" element={<Home walletAddress={walletAddress} />} />
              <Route path="/deposit" element={<Deposit contract={contract} />} />
              <Route path="/transfer" element={<Transfer contract={contract} />} />
              <Route path="/assign-voter" element={<AssignVoter contract={contract} />} />
              <Route path="/vote-new-owner" element={<VoteNewOwner contract={contract} />} />
              <Route path="/set-limit" element={<SetLimit contract={contract} />} />
              <Route path="/admin" element={<Admin contract={contract} />} />
              <Route path="/user-dropdown" element={<UserDropdown contract={contract} />} />
              <Route path="*" element={<Navigate to="/" />} />
            </Routes>
          </motion.div>
        )}
      </AnimatePresence>
    </Router>
  );
}

function NavBar() {
  return (
    <nav className="navbar">
      <div className="nav-items">
        <Link className="nav-item" to="/">Home</Link>
        <Link className="nav-item" to="/deposit">Deposit</Link>
        <Link className="nav-item" to="/transfer">Transfer</Link>
        <Link className="nav-item" to="/assign-voter">Assign Voter</Link>
        <Link className="nav-item" to="/vote-new-owner">Vote New Owner</Link>
        <Link className="nav-item" to="/set-limit">Set Limit</Link>
        <Link className="nav-item" to="/admin">Admin</Link>
      </div>
    </nav>
  );
}

function UserDropdown({ walletAddress, accountBalance, showDropdown, setShowDropdown }) {
  return (
    <div className="user-dropdown">
      <button className="user-btn" onClick={() => setShowDropdown(!showDropdown)}>
        {walletAddress.slice(0, 6)}...{walletAddress.slice(-4)}
      </button>
      {showDropdown && (
        <motion.div 
          className="dropdown-content"
          initial={{ opacity: 0, y: -10 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5, ease: "easeOut" }}
        >
          <p>Balance: {accountBalance} ETH</p>
        </motion.div>
      )}
    </div>
  );
}

export default App;
