// src/components/Transfer.js
import React, { useState } from 'react';
import { ethers } from 'ethers';
import { motion } from 'framer-motion';

function Transfer({ contract, contractBalance }) {
  const [transferTo, setTransferTo] = useState("");
  const [transferAmount, setTransferAmount] = useState("");

  async function handleTransfer() {
    try {
      const tx = await contract.transferFunds(
        transferTo,
        ethers.utils.parseEther(transferAmount),
        { value: 0 }
      );
      await tx.wait();
      alert("Transfer successful!");
    } catch (err) {
      console.error(err);
      alert("Transfer failed!");
    }
  }

  return (
    <div className="transfer-container">
      <div className="transfer-left">
      <h1>Transfer money across the world</h1>
      </div>
      <div class="total-deposit">
                <motion.div
                  key={userDepositTotal}  // Changing key triggers re-mount & animation
                  {...animationProps}
                  className="total-deposit"
                >
                  <h3>Current Wallet Balance: {contractBalance}</h3>
                  </motion.div>
                </div>
      <div className="transfer-right">
        <motion.img 
          src="/transfer-new.gif" 
          alt="Transfer Animation" 
          style={{ width: "250px", height: "250px", marginBottom: "0px" }}
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.2, duration: 1, ease: "easeInOut" }}
        />
        <h2>Transfer Funds</h2>
        <div className='glass-container'>
        <input class = "glass-input"
          type="text" 
          placeholder="Recipient Address" 
          value={transferTo} 
          onChange={(e) => setTransferTo(e.target.value)} 
        />
        <input class = "glass-input"
          type="text" 
          placeholder="Amount in ETH" 
          value={transferAmount} 
          onChange={(e) => setTransferAmount(e.target.value)} 
        />
        <button onClick={handleTransfer}>Transfer</button>
        </div>
      </div>
    </div>
  );
}

export default Transfer;
