// src/components/Deposit.js
import React, { useState } from 'react';
import { ethers } from 'ethers';
import { motion } from 'framer-motion';

function Deposit({ contract }) {
  const [depositAmount, setDepositAmount] = useState("");

  // const Input = ({placeholder, name, type, value, handleChange}) => ( 
  //   <input 
  //     placeholder ={palceholder}
  //     type={type}
  //     step="0.0001
  //     value={value}
  //     onChange={(e) => handleChange(e, name)}
  //     className="my-2 w-full rounded-sm p-2 outline-none bg-transparent text-white border-none text-small white-glassmorphism"
  //   />
  // );

  async function handleDeposit() {
    try {
      const tx = await contract.deposit({
        value: ethers.utils.parseEther(depositAmount)
      });
      await tx.wait();
      alert("Deposit successful!");
    } catch (err) {
      console.error(err);
      alert("Deposit failed!");
    }
  }

  return (
    <div className="deposit-container">
      <motion.img 
        src="/deposit-new.gif" 
        alt="Deposit Animation" 
        style={{ width: "150px", height: "150px", marginBottom: "20px" }}
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.2, duration: 1, ease: "easeInOut" }}
      />
      <h2>Deposit Funds</h2>
      <div class="glass-container">
      <input class = "glass-input"
        type="text" 
        placeholder="Amount in ETH" 
        value={depositAmount} 
        onChange={(e) => setDepositAmount(e.target.value)} 
      />

      {/* <input 
        type="text" 
        placeholder="Amount in ETH" 
        value={depositAmount} 
        onChange={(e) => setDepositAmount(e.target.value)} 
      /> */}
      <button clsasName="glass-button" onClick={handleDeposit}>Deposit</button>
      </div>
    </div>
  );
}

export default Deposit;
